"use strict";(self.webpackChunkdocs=self.webpackChunkdocs||[]).push([[6818],{56296:e=>{e.exports=JSON.parse('{"functions":[{"name":"Disconnect","desc":"Disconnects the signal connection. After calling this, the `Connection` object becomes unusable and attempting to access its properties or methods will error.","params":[],"returns":[],"function_type":"method","source":{"line":69,"path":"impl/Types/Connection.luau"}},{"name":"Enable","desc":"Enables the connection if it was previously disabled.","params":[],"returns":[],"function_type":"method","source":{"line":78,"path":"impl/Types/Connection.luau"}},{"name":"Disable","desc":"Disables the connection without disconnecting it. The connection can be re-enabled later using `Enable` or by setting the `Enabled` property to true.","params":[],"returns":[],"function_type":"method","source":{"line":87,"path":"impl/Types/Connection.luau"}},{"name":"Fire","desc":"Fires the connected function immediately with the provided arguments. Does nothing if `ForeignState` is true. If `IsOnce` is true, the connection will be disconnected after firing.","params":[{"name":"...","desc":"Arguments to pass to the connected function.","lua_type":"any"}],"returns":[],"function_type":"method","source":{"line":97,"path":"impl/Types/Connection.luau"}},{"name":"Defer","desc":"Schedules the connected function to be executed later (deferred) with the provided arguments via `task.defer`. Does nothing if `ForeignState` is true. If `IsOnce` is true, the connection will be disconnected after firing.","params":[{"name":"...","desc":"Arguments to pass to the connected function when deferred.","lua_type":"any"}],"returns":[],"function_type":"method","source":{"line":107,"path":"impl/Types/Connection.luau"}}],"properties":[{"name":"ForeignState","desc":"(Read-Only) A boolean indicating if the connection belongs to a different Luau Virtual Machine (e.g., an Actor thread) or is a C function connection.","lua_type":"boolean","readonly":true,"source":{"line":18,"path":"impl/Types/Connection.luau"}},{"name":"Function","desc":"(Read-Only) The Luau function associated with this connection. Returns `nil` if `ForeignState` is true.","lua_type":"((...any) -> ...any)?","readonly":true,"source":{"line":27,"path":"impl/Types/Connection.luau"}},{"name":"Enabled","desc":"(Read/Write) A boolean indicating if the connection is currently enabled. Setting this to `false` is equivalent to calling `Disable`, setting it to `true` is equivalent to calling `Enable`.","lua_type":"boolean","source":{"line":35,"path":"impl/Types/Connection.luau"}},{"name":"LuaConnection","desc":"(Read-Only) A boolean indicating if this is a connection to a Luau function (as opposed to a C function).","lua_type":"boolean","readonly":true,"source":{"line":44,"path":"impl/Types/Connection.luau"}},{"name":"Thread","desc":"(Read-Only) The Luau thread (coroutine) this connection belongs to. Returns `nil` if `ForeignState` is true.","lua_type":"thread?","readonly":true,"source":{"line":53,"path":"impl/Types/Connection.luau"}},{"name":"IsOnce","desc":"(Read-Only) A boolean indicating if this connection was created using `ConnectOnce`. Such connections automatically disconnect after firing once.","lua_type":"boolean","readonly":true,"source":{"line":62,"path":"impl/Types/Connection.luau"}}],"types":[],"name":"Connection","desc":"Represents a connection object obtained from `instances.getconnections`.\\n\\nThis object provides detailed information about a single connection to an `RBXScriptSignal` and allows manipulating it.","tags":["Type"],"source":{"line":9,"path":"impl/Types/Connection.luau"}}')}}]);